package adventofcode.aoc2022;

import java.text.MessageFormat;
import java.util.HashSet;
import java.util.Set;

public class Day06 {

	public static void main(String[] args) {
		String test1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
		String test2 = "bvwbjplbgvbhsrlpgdmjqwftvncz";
		String test3 = "nppdvjthqldpwncqszvftbrmjlhg";
		String test4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg";
		String test5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw";
		String input = "";

		run(test1, 4);
		run(test2, 4);
		run(test3, 4);
		run(test4, 4);
		run(test5, 4);
		run(input, 4);

		run(test1, 14);
		run(test2, 14);
		run(test3, 14);
		run(test4, 14);
		run(test5, 14);
		run(input, 14);

	}

	private static void run(String input, int charCount) {
		int i = charCount - 1;

		while (i < input.length()) {
			Set<Character> k = new HashSet<>();
			for (int j = i - charCount + 1; j <= i; j++) {
				k.add(input.charAt(j));
			}
			if (k.size() == charCount)
				break;
			i++;
		}

		System.out.println(MessageFormat.format("marker-{0}: {1}", charCount, i + 1));
	}

}
